extern int pc_correct ( int newPos, int k, int rows, unsigned char ** data, int vLen ) ;

int gf_2vect_pss_neon(int len, int k, unsigned char *g_tbls, unsigned char **data,
        unsigned char ** dest, int offSet)
{
        int curSym, curPos = 0 ;
        uint8x16_t parity0 [ 2 ], parity1 [ 2 ], parity2 [ 2 ], parity3 [ 2 ] ;
        uint8x16_t taps [ 1 ], tapsh [ 1 ] ;
        uint8x16_t data_vec, data_vech, temp ;

        taps  [ 0 ] = vld1q_u8 ( g_tbls + ( 0 * 32 ) ) ;
        tapsh [ 0 ] = vld1q_u8 ( g_tbls + ( 0 * 32 ) + 16 ) ;

        for ( curPos = offSet ; curPos < len ; curPos += 64 )
        {
                data_vec = vld1q_u8( &data [ 0 ] [ curPos + 0 * 16 ] ) ;
                parity0 [ 0 ] = data_vec ;
                parity0 [ 1 ] = data_vec ;
                data_vec = vld1q_u8( &data [ 0 ] [ curPos + 1 * 16 ] ) ;
                parity1 [ 0 ] = data_vec ;
                parity1 [ 1 ] = data_vec ;
                data_vec = vld1q_u8( &data [ 0 ] [ curPos + 2 * 16 ] ) ;
                parity2 [ 0 ] = data_vec ;
                parity2 [ 1 ] = data_vec ;
                data_vec = vld1q_u8( &data [ 0 ] [ curPos + 3 * 16 ] ) ;
                __builtin_prefetch ( &data [ 0 ] [ curPos + 64 ], 0, 3 ) ;
                parity3 [ 0 ] = data_vec ;
                parity3 [ 1 ] = data_vec ;

                for ( curSym = 1 ; curSym < k ; curSym ++ )
                {
                        data_vec = vld1q_u8( &data [ curSym ] [ curPos + 0 * 16 ] ) ;
                        data_vech = vshrq_n_u8 ( data_vec, 4 ) ;
                        parity0 [ 1 ] = veorq_u8 ( parity0 [ 1 ], data_vec ) ;
                        temp = vmovq_n_u8 ( 0x0f ) ; 
                        data_vec = vandq_u8 ( data_vec, temp ) ;

                        temp = vqtbl1q_u8 ( taps [ 0 ], data_vec ) ;
                        parity0 [ 0 ] = veorq_u8(parity0 [ 0 ], temp ) ;
                        temp = vqtbl1q_u8 ( tapsh [ 0 ], data_vech ) ;
                        parity0 [ 0 ] = veorq_u8(parity0 [ 0 ], temp ) ;

                        data_vec = vld1q_u8( &data [ curSym ] [ curPos + 1 * 16 ] ) ;
                        data_vech = vshrq_n_u8 ( data_vec, 4 ) ;
                        parity1 [ 1 ] = veorq_u8 ( parity1 [ 1 ], data_vec ) ;
                        temp = vmovq_n_u8 ( 0x0f ) ; 
                        data_vec = vandq_u8 ( data_vec, temp ) ;

                        temp = vqtbl1q_u8 ( taps [ 0 ], data_vec ) ;
                        parity1 [ 0 ] = veorq_u8(parity1 [ 0 ], temp ) ;
                        temp = vqtbl1q_u8 ( tapsh [ 0 ], data_vech ) ;
                        parity1 [ 0 ] = veorq_u8(parity1 [ 0 ], temp ) ;

                        data_vec = vld1q_u8( &data [ curSym ] [ curPos + 2 * 16 ] ) ;
                        data_vech = vshrq_n_u8 ( data_vec, 4 ) ;
                        parity2 [ 1 ] = veorq_u8 ( parity2 [ 1 ], data_vec ) ;
                        temp = vmovq_n_u8 ( 0x0f ) ; 
                        data_vec = vandq_u8 ( data_vec, temp ) ;

                        temp = vqtbl1q_u8 ( taps [ 0 ], data_vec ) ;
                        parity2 [ 0 ] = veorq_u8(parity2 [ 0 ], temp ) ;
                        temp = vqtbl1q_u8 ( tapsh [ 0 ], data_vech ) ;
                        parity2 [ 0 ] = veorq_u8(parity2 [ 0 ], temp ) ;

                        data_vec = vld1q_u8( &data [ curSym ] [ curPos + 3 * 16 ] ) ;
                        __builtin_prefetch ( &data [ curSym ] [ curPos + 64 ], 0, 3 ) ;
                        data_vech = vshrq_n_u8 ( data_vec, 4 ) ;
                        parity3 [ 1 ] = veorq_u8 ( parity3 [ 1 ], data_vec ) ;
                        temp = vmovq_n_u8 ( 0x0f ) ; 
                        data_vec = vandq_u8 ( data_vec, temp ) ;

                        temp = vqtbl1q_u8 ( taps [ 0 ], data_vec ) ;
                        parity3 [ 0 ] = veorq_u8(parity3 [ 0 ], temp ) ;
                        temp = vqtbl1q_u8 ( tapsh [ 0 ], data_vech ) ;
                        parity3 [ 0 ] = veorq_u8(parity3 [ 0 ], temp ) ;

                }

                temp = vorrq_u8 ( parity0 [ 0 ], parity1 [ 0 ] ) ;
                temp = vorrq_u8 ( parity0 [ 0 ], parity2 [ 0 ] ) ;
                temp = vorrq_u8 ( parity0 [ 0 ], parity3 [ 0 ] ) ;
                uint32x4_t tmp = vreinterpretq_u32_u8( temp ) ;
                if ( vmaxvq_u32 ( tmp ) != 0 )
                {
                        vst1q_u8 ( &dest [ 0 ] [ 0 * 16 ], parity0 [ 0 ] ) ;
                        vst1q_u8 ( &dest [ 0 ] [ 1 * 16 ], parity1 [ 0 ] ) ;
                        vst1q_u8 ( &dest [ 0 ] [ 2 * 16 ], parity2 [ 0 ] ) ;
                        vst1q_u8 ( &dest [ 0 ] [ 3 * 16 ], parity3 [ 0 ] ) ;
                        vst1q_u8 ( &dest [ 1 ] [ 0 * 16 ], parity0 [ 1 ] ) ;
                        vst1q_u8 ( &dest [ 1 ] [ 1 * 16 ], parity1 [ 1 ] ) ;
                        vst1q_u8 ( &dest [ 1 ] [ 2 * 16 ], parity2 [ 1 ] ) ;
                        vst1q_u8 ( &dest [ 1 ] [ 3 * 16 ], parity3 [ 1 ] ) ;
                        return ( curPos ) ;
                }
        }
        return ( curPos ) ;
}

